  public function removerPet($id_pet){
    $pet = new Pets;
    $data['usuario']  =  session()->get('id_usuario');
    $exclusao = $pet->setExcluido($id_pet, $data['usuario']);

    if($exclusao == 1){
      $data['exclusao'] = 1;
      $data['status']   = 1;
      $data['mensagem']   = 'Sucesso na exclusÃ£o';
    }else{
      $data['exclusao'] = 0;
      $data['status']   = 0;
      $data['mensagem']   = 'Nada aconteceu';
    }
    return $this->respond($data);
  }
  public function alterarStatusPet($id_pet){
    $pet = new Pets;
    $data['usuario']  =  session()->get('id_usuario');
    $status = $pet->getAdotado($id_pet);
    if($status->adotado == 0){
      $pet->setAdotado($id_pet,$data['usuario'],1);
      $data['status']   = 1;
    }elseif($status->adotado  == 1){
      $pet->setAdotado($id_pet,$data['usuario'],0);
      $data['status']   = 0;
    }
   return $this->respond($data);
  }
  public function solicitarAdocao(){

    $data['telefone']           = $this->request->getPostGet('telefone');
    $data['telefone_protetor']  = session()->get('telefone_usuario');
    $data['email_usuario']      = session()->get('email_usuario');
    $data['nome_protetor']      = $this->request->getPostGet('nome_protetor');
    $data['id_pet']             = $this->request->getPostGet('id_pet');
    $data['nome_pet']           = $this->request->getPostGet('nome_pet');
    $data['nome_interessado']   = $this->request->getPostGet('nome_interessado');
    $data['msg_opcional']       = $this->request->getPostGet('msg_opcional');

    if(empty($data['msg_opcional'])){
      $data['msg_opcional'] = 'NÃ£o deixou recado.';
    }
    if(empty($data['telefone'])){
      $data['status']   = 2;
      $data['mensagem'] = '* Ã‰ Obrigatorio o Telefone';  
    }else if(session()->has('criptopost')){

      $mensagem = new Sima;
      $mensagem->enviarMensagemWa($data['telefone_protetor'] , 'Oieeee '.$data['nome_protetor'].' a(o) '.$data['nome_interessado'].' estÃ¡ interessado em adotar o pet '.$data['nome_pet'].' Telefone: '.$data['telefone'].' Mensagem: '.$data['msg_opcional']);

      $mensagem->enviarMensagemEmail('SolicitaÃ§Ã£o de AdoÃ§Ã£o','Oieeee '.$data['nome_protetor'].' a(o) '.$data['nome_interessado'].' estÃ¡ interessado em adotar o pet '.$data['nome_pet'].' Telefone: '.$data['telefone'].' Mensagem: '.$data['msg_opcional'],$data['email_usuario']);
      $data['status']   = 1;
      $data['mensagem'] = 'Mensagem enviada com sucesso!';

    }else{
      $data['status']   = 4;
      $data['mensagem'] = 'Espertinho vocÃª nÃ£o ? VÃ¡ Hackear em outro lugar';
    }


    return $this->respond($data);
  }


  public function getPets($limit){
    $model = new Pets();
